import React, { useContext, useState, useEffect } from "react";
import { LoginContext } from "../Contexts/LoginContext";
import { TodoContext } from "../Contexts/TodoContext";
import Todoinput from "../Componentss/Todoinput";
import TodoList from "../Componentss/Todolist";
import '../Styles/Todo.css';

function Todo() {
  const { username } = useContext(LoginContext);
  const [listTodo, setListTodo] = useState([]);

  useEffect(() => {
    const storedTasks = JSON.parse(localStorage.getItem(username))?.tasks || [];
    setListTodo(storedTasks);
  }, [username]);
//parent component mein useEffect use kr diya hai
  return (
    <TodoContext.Provider value={{ listTodo, setListTodo }}>
      <div className="center-container">
        <h1>Hi {username}ðŸ‘‹!</h1>
        <Todoinput />
        <TodoList />
      </div>
    </TodoContext.Provider>
  );
}

export default Todo;
