    .then(response=>{
      const updatedTasks=listTodo.map(task=>task.id===index?{...task,done:updatedDone}:task);
      setListTodo(updatedTasks);
    })
    .catch(error=>{
      console.error("error updating the task",error);
    })
  };

  return (
    <div className="todo-list">
      {sortTaskPriority(listTodo).map((task, index) => (
        <div className={`note ${task.done ? 'done' : ''}`} key={index} style={{ backgroundColor: task.color }}>
          <p>{task.text}</p>
          <div className="btns">
          <button className={`${task.done?"btn-1":"btn-2"}`} onClick={() => handleDone(index)}><IoMdDoneAll /></button>
          <button className="btn-2" onClick={() => handleDeleteTodo(index)}>X</button>
          </div>
        </div>
      ))}
    </div>
  );
}

export default TodoList;
